catalog_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   //単価の2桁小数の正規表現
   var regex = /^\d+(\.\d{1,2})?$/;
   var unit_price = g_form.getValue('v_unit_price');

   //数量の0以外整数の正規表現
   var regex_num = /^-?[1-9]\d*$/;
   var unit_num = g_form.getValue('v_unit_num'); 

   //単価未入力のチェック
   if(unit_price ==''){
       g_form.showFieldMsg('v_unit_price','単価を入力してください。','error');
       return false;
   //単価の小数点以下2桁をオーバーした時チェック
   } else if(!regex.test(unit_price)){
       g_form.showFieldMsg('v_unit_price','2桁小数まで入力してください。','error');
       return false;
   //単価の小数点以下2桁未満の時、2桁補足
   } else {
       var format_unit_price = parseFloat(unit_price).toFixed(2);
       g_form.setValue('v_unit_price',format_unit_price);
   }
   //数量未入力のチェック
   if(unit_num ==''){
       g_form.showFieldMsg('v_unit_num','数量を入力してください。','error');
       return false;
   //数量の
   } else if(!regex_num.test(unit_num)){
       g_form.showFieldMsg('v_unit_num','0より大きい数字を入力してください。','error');
       return false;
   }
}
