sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var task_num = inputs.task_num;
var gr = new GlideRecord('x_846092_head_and_target_detail');/*明細テーブル*/
gr.addQuery('v_task_num', task_num);
gr.orderBy('sys_created_on');
gr.query();
let count = 0;    
while (gr.next()) {
            count += 1;//明細枝番を+1採番
            //gr.v_task_num = task_num;
            gr.v_detail_no = count;
            gr.update();
        }
    }
)(inputs, outputs);

